import React ,{useState} from "react";
import './login.css'
import {Link} from 'react-router-dom';


function Login(){

    const [contact, setContact] = useState({
 
        email:'',
        password:'',
  
        
    })
    
    const handleChange = (event)=>{
        const {name, value} = event.target
        setContact ((preValue)=>{  
        return {
        ...preValue,
        [name]: value
        }
        })
    }




return(
<div className="login">
<Link to = '/Sign'><button className="signup">Sign Up</button></Link>

<h2 style={{float:"left"} } >Your email</h2>
<input type="text" className="email2" name="email" value={contact.email}></input>

<h2 style={{float:"left"}}>Your password</h2>
<input type="password" className="password2" name="password" value={contact.password}></input>

<button className="login2">Login</button>



</div>
)
}



export default Login
