import React, { useState } from "react";
import { Link } from "react-router-dom";
import { getStorage, ref, getDownloadURL } from "firebase/storage";
import { collection, getDocs } from "firebase/firestore";

import { app, db } from "../utils/firebase.js";

import "./tutorial.css";

const storage = getStorage(app);
const videosInfo = collection(db, "videosInfo");

function Tutorial() {
    const [videoFlag, setVideoFlag] = useState(0);
    const [videosAry, setVideosAry] = useState([]);

    getDocs(videosInfo).then(result => {
        const videos = [];
        result.docs.forEach(doc => {
            const obj = {};
            let val = doc._document.data.value.mapValue.fields;
            obj.view = val.view.integerValue;
            obj.rating = val.rating.integerValue;
            obj.ref = val.ref.stringValue;
            videos.push(obj);
        });
        videos.forEach(video => {
            const reference = ref(storage, `videos/${video.ref}`);
            getDownloadURL(reference).then(url => {
                video.url = url;
            }).catch(error => {
                console.log(error);
            })
        })
        return videos;
    }).then(videos => {
        setVideosAry(videos);
    }).catch(error => {
        console.log(error);
    })

    return (
        <div className="tutorial">
            <div className="nav">
                <Link to='/'><span>Home</span></Link>
                <Link to='/findQuestion'><span>find question</span></Link>
                <Link to='/tutorial'><span>tutorial</span></Link>
            </div>
            <div className="videos">
                <video className="videoItem" src={videosAry[videoFlag]?.url} controls preload="metadata"></video>
                <div className="videoData">
                    <span>view：0</span>
                    <span>rating：0</span><button>good</button>
                </div>
            </div>
            <div className="videosPre">
                {
                    videosAry.map(video => {
                        return <div key={video.ref}>{video.ref}</div>
                    })
                }
                <video className="preItem" src="" preload="metadata"></video>
            </div>
        </div>
    )
}

export default Tutorial;
