import React, { useState } from "react";
import "./Artical.css"
function Artical() {
    const [title, setTitle] = useState("");
    const [abstract, setAbstract] = useState("");

    function postArtical(e) {
        console.log(title);
    }

    return (
        <div>
            <div className="t1">
                <h4>Title:</h4>
                <input value={title} onChange={(e) => { setTitle(e.target.value) }} className="Title" placeholder="Enter a descriptive title" />
            </div>
            <div className="t2">
                <h4 style={{ float: "left" }}>Abstract</h4>
                <textarea value={abstract} placeholder="Enter a 1-paragraph abstract" className="abstract" />
            </div>
            <div className="t4">
                <h4 style={{ float: "left" }}>Artical Text</h4>
                <textarea placeholder="Enter a 1-paragraph abstract" className="text" />
            </div>
            <div className="t5">
                <h4>Tags:</h4>
                <input className="Title" placeholder="Please add up to 3 tags describe what your question is about e.g.,Java " />
            </div>
            <button className="post" onClick={postArtical}>Post</button>
        </div>
    )
}

export default Artical
