import React, { useState, useEffect } from "react";
import { db } from "./utils/firebase.js";
import { collection, getDocs, query, where } from "firebase/firestore";

import "./findQuestion.css"
let flag = 1;

function FindQuestion() {
  const [questions, setQuestions] = useState([]);
  useEffect(() => {
    if (flag) {
      const Q_A = collection(db, "Q_A");
      let questions = [];

      const q = query(Q_A, where("type", "==", 0));
      getDocs(q).then(result => {
        result.docs.forEach(doc => {
          const obj = {};
          let val = doc._document.data.value.mapValue.fields;
          obj.title = val.title.stringValue;
          obj.describe = val.describe.stringValue;
          obj.tags = val.tags.arrayValue.values;
          obj.isHide = true;
          questions.push(obj);
        })
        flag = 0;
        setQuestions(questions);
      }).catch(error => { });
    }
  })

  function dragStart(event) {
    event.dataTransfer.setData("id", event.target.id);
  }

  function dragOver(event) {
    event.preventDefault();
    event.dataTransfer.dropEffect = "move";
  }

  return (
    <div className="find-question">
      <div className="find-box">
        <input type="text" placeholder="please input title,tag or date to find question." />
        <button>filter out</button>
      </div>
      <div className="questions-box" onDragOver={() => { dragOver() }} >
        {
          questions.map((question, index) => {
            return (
              <div className="question-item" key={question.title} draggable onDragStart={(e) => { dragStart(e) }}>
                <h3 onClick={() => { questions[index].isHide = false; setQuestions(questions.slice()); }}>{question.title}</h3>
                {question.isHide === false &&
                  <div>
                    <div>{question.describe}</div>
                    <div onClick={() => { questions[index].isHide = true; setQuestions(questions.slice()); }} style={{ "textDecoration": "underline" }}>hide</div>
                  </div>
                }
              </div>
            )
          })
        }
      </div>
    </div >
  );
}

export default FindQuestion;
