import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { db } from "../utils/firebase.js";
import { collection, getDocs, query, where, doc, updateDoc, arrayUnion, arrayRemove } from "firebase/firestore";

import "./myHome.css";

const userInfo = JSON.parse(sessionStorage.getItem("userInfo"));

let flag = 1;

function MyHome() {

    const [message, setMessage] = useState([]);

    useEffect(() => {
        if (flag) {
            const User = collection(db, "User");
            const q = query(User, where("email", "==", userInfo.email));
            getDocs(q).then(result => {
                let messageAry = result.docs[0]._document.data.value.mapValue.fields.message.arrayValue.values;
                console.log(messageAry);
                let message = [];
                messageAry.forEach((element, index) => {
                    if (element.mapValue.fields.status.stringValue === "1") {
                        const obj = {};
                        obj.index = index;
                        obj.time = element.mapValue.fields.time.integerValue;
                        obj.video = element.mapValue.fields.video.stringValue;
                        obj.status = element.mapValue.fields.status.stringValue;
                        message.push(obj);
                    }
                });
                flag = 0;
                setMessage(message);
            }).catch(error => { });
        }
    });

    function detail(item) {
        console.log(item);
        const washingtonRef = doc(db, "User", userInfo.id);

        updateDoc(washingtonRef, {
            message: arrayUnion({
                time: item.time,
                video: item.video,
                status: 0
            })
        }).then(() => {
            updateDoc(washingtonRef, {
                message: arrayRemove(message[item.index])
            })
        }).catch(error => {
            console.log(error);
        })
    }

    return (
        <div className="my-home">
            <div className="nav">
                <Link to='/'><span>Home</span></Link>
                <Link to='/findQuestion'><span>find question</span></Link>
                <Link to='/tutorial'><span>tutorial</span></Link>
            </div>
            <div className="userInfo">
                <h3>个人信息</h3>
                {
                    ["username", "email"].map(info => {
                        return <div>{info}: {userInfo[info]}</div>;
                    })
                }
            </div>

            <div className="myMessage">
                <h3>System Message</h3>
                {
                    message.map(item => {
                        return <div className="messageItem" onClick={() => { detail(item) }}>
                            <span>time: {(new Date(Number(item.time))).toString()}</span>
                            <span>type: good</span>
                            <span style={{ color: "green" }}>new message!</span>
                        </div>;
                    })
                }
            </div>
        </div>
    );
}

export default MyHome
