import React, { useState, useEffect } from "react";
import { db } from "./utils/firebase.js";
import { collection, getDocs, query, where } from "firebase/firestore";

import "./findQuestion.css"

function FindQuestion() {
  const [Questions] = useState(questions);
  useEffect(() => {
    const Q_A = collection(db, "Q_A");
    let questions = [];

    const q = query(Q_A, where("type", "==", 0));
    getDocs(q).then(result => {
      const obj = {};
      result.docs.forEach(doc => {
        let val = doc._document.data.value.mapValue.fields;
        obj.title = val.title.stringValue;
        obj.describe = val.describe.stringValue;
        obj.tags = val.tags.arrayValue.values;
        questions.push(obj);
      })
    }).catch(error => { });
  })
  return (
    <div className="find-question">
      <div className="find-box">
        <input type="text" placeholder="please input title,tag or date to find question." />
        <button>filter out</button>
      </div>
      <div className="questions-box">
        {
          Questions.map(question => {
            return (
              <div className="question-item">
                <h3>hello</h3>
                <div>hello hello hello hello hello hello hello hello hello hello hello hello hello hello hello hello hello hello hello hello hello hello hello hello hello hello hello hello hello hello hello hello hello hello hello hello hello hello hello hello hello hello hello hello hello hello hello hello </div>
              </div>
            )
          })
          // [1, 2].map(question => {
          //   return (
          //     <div>1</div>
          //   )
          // })
        }
      </div>
    </div>
  );
}

export default FindQuestion;
